// library for address array 
library AddressArrayLib {
    using AddressArrayLib for addresses;

    struct addresses {
        address[] array;
    }

    function add(addresses storage self, address _address)
        external
    {
        if(! exists(self, _address)){
            self.array.push(_address);
        }
    }

    function getIndexByAddress(
        addresses storage self,
        address _address
    ) internal view returns (uint256, bool) {
        uint256 index;
        bool exists_;

        for (uint256 i = 0; i < self.array.length; i++) {
            if (self.array[i] == _address) {
                index = i;
                exists_ = true;

                break;
            }
        }
        return (index, exists_);
    }

    function remove(
        addresses storage self,
        address _address
    ) internal {
       for (uint256 i = 0; i < self.array.length; i++) {
            if (
                self.array[i] == _address 
            ) {
                delete self.array[i];
            }
        }
    }


    function exists(
        addresses storage self,
        address _address
    ) internal view returns (bool) {
        for (uint256 i = 0; i < self.array.length; i++) {
            if (
                self.array[i] == _address 
            ) {
                return true;
            }
        }
        return false;
    }
}

/**
 * @title Staking contract
 * @author Yogesh Singh
 * @notice This contract will store and manage staking at APY defined by owner
 * @dev Store, calculate, collect and transefer stakes and rewards to end user
 */
contract Staking is Ownable{

    // Lib for uints
    using SafeMath for uint;

    // Custom lib for managing address[]
    using AddressArrayLib for AddressArrayLib.addresses;

    // Sec in a year 
    uint private APYTime; // = 365 days (For testing it can be updated to shorter time.)

    // Structure to store StakeHoders details
    struct  stakeDetails {
        uint stake; // Total amount staked by the user for perticular pool
        uint reward; // Total unclaimed reward calculated at lastRewardCalculated 
        uint APY; // APY at which the amount was staked
        uint lastRewardCalculated; // The time when reward was calculated
    }

    // Bool for initilizer, used for proxy contract
    bool private initialized;

    /** mapping to store current status for stakeHolder
     * Explaination:
     *  {
     *      Staker: {
     *           Pool: staking details
     *      }
     *  }
     */
    mapping(
        address => mapping(
            uint => stakeDetails
        )
    ) public stakeHolders;

    /** Store all the stakeholders
     * {
     *   PoolId: stakeholdres' list
     * }
     */
    mapping(uint => AddressArrayLib.addresses) stakers;

    // Events
    event Staked(
        address indexed staker,
        uint amount,
        uint indexed _poolId,
        uint timestamp
    );
    event Unstaked(
        address indexed staker,
        uint amount,
        uint indexed _poolId,
        uint timestamp
    );
    event RewardClaimed(
        address indexed staker,
        uint amount,
        uint indexed _poolId,
        uint timestamp
    );

    // Structure to store the pool's information
    struct stakingPool {
        address token; // Address of staking token
        address reward; // Address of reward token
        uint APY; // APY for this perticular pool
        uint tvl; // Total value currently locked in this pool
        uint totalAllotedReward; // Total award transfered to this contract by admin for reward.
        uint totalClaimedReward; // Total reward claimed in this pool
    }

    // List of pools created by admin
    stakingPool[] public pools; 

    /**
     * @dev Modifier to check if pool exists
     * @param _poolId Pools's ID
     */
    modifier poolExists(uint _poolId) {
        require(_poolId < pools.length, "Staking: Pool doesn't exists");
        _;
    }

    /**
     * @dev This function will create new pool, access type is onlyOwner
     * @notice This Function will create new pool with the token address,\
       reward address and the APY percentage.
     * @param _token Staking token address for this pool. 
     * @param _reward Staking reward token address for this pool
     * @param _APY APY percentage * 1000 for this pool.
     */
    function addPool(address _token, address _reward, uint _APY) public onlyOwner{
        // Check if pool already exists
        for (uint i=0; i<pools.length; i++){
            if( pools[i].token == _token && pools[i].reward == _reward){
                revert("Pool already exists");
            }
        }

        // Add pool to contract
        pools.push(stakingPool(_token, _reward, _APY, 0, 0, 0));
    }

    /**
     * @dev This funtion will return the length of pools\
        which will be used to loop and get pool details.
     * @notice Get the length of pools and use it to loop for index.
     * @return Length of pool.
     */
    function poolLength() public view returns(uint){
        return pools.length;
    }

    /**
     * @dev This function allows owner to update APY for specific pool.
     * @notice Let's you update the APY for this pool if you're current owner.
     * @param _poolId pool's Id in which you want to update the APY.
     * @param _APY New APY percentage * 1000.
     */
    function updateAPY(uint _poolId, uint _APY) public onlyOwner poolExists(_poolId){
        pools[_poolId].APY = _APY;
    }

    /**
     * @dev This funciton allows owner to add more reward amount to  this contract.
     * @notice Let's you allot more reward fund in this contract.
     * @param _poolId pool's Id in which you want to add this reward.
     * @param amount amount to be transfered from owner's wallet to this contract.
     */
    function addRewardToPool(uint _poolId, uint amount) public onlyOwner poolExists(_poolId){
        // Reward contract object.
        IERC20 rewardToken = IERC20(pools[_poolId].reward);

        // Check if amount is allowed to spend the token
        require(rewardToken.allowance(msg.sender, address(this)) >= amount, "Staking: Must allow Spending");

        // Transfer the token to contract
        rewardToken.transferFrom(msg.sender, address(this), amount);

        // Update the pool's stats
        pools[_poolId].totalAllotedReward += amount;
    }

    /**
     * @notice This method will be called once only by proxy contract to init.
     */
    function init() public{
        require(!initialized, "Already initilized.");
        initOwner();
        APYTime = 365 days;
        initialized = true;
    }

    /**
     * @dev This function is used to calculate current reward for stakeHolder
     * @param _poolId The pool for which we want to calculate reward.
     * @param _stakeHolder The address of stakeHolder to calculate reward till current block
     * @return reward calculated till current block
     */
    function _calculateReward(uint _poolId, address _stakeHolder) internal view returns(uint reward){
        stakeDetails memory stakeDetail = stakeHolders[_stakeHolder][_poolId];


        if (stakeDetail.stake > 0){
            // Without safemath formula for explanation
            // reward = (
            //     (stakeDetail.stake * stakeDetails.APY * (block.timestamp - stakeDetail.lastRewardCalculated)) /
            //     (APYTime * 100 * 1000)
            // );

            reward = stakeDetail.stake.mul(stakeDetail.APY).mul(
                block.timestamp.sub(stakeDetail.lastRewardCalculated)
            ).div(
                APYTime.mul(100).mul(1000)
            );
        }
        else{
            reward = 0;
        }
    }

    /**
     * @dev This function is used to calculate Total reward for stakeHolder for pool
     * @param _poolId The pool for which we want to calculate reward.
     * @param _stakeHolder The address of stakeHolder to calculate Total reward 
     * @return reward total reward 
     */
    function calculateReward(uint _poolId, address _stakeHolder) public view returns(uint reward){
        stakeDetails memory stakeDetail = stakeHolders[_stakeHolder][_poolId];
        reward = stakeDetail.reward + _calculateReward(_poolId, _stakeHolder);
    }

    /**
     * @dev Allows user to stake the amount the pool. Calculate the old reward\
       and updates the reward, staked amount and current APY.
     * @notice This function will update your staked amount.
     * @param _poolId The pool in which user wants to stake.
     * @param amount The amount user wants to add into his stake.
     */
    function stake(uint _poolId, uint amount) public poolExists(_poolId){ 

        IERC20 token = IERC20(pools[_poolId].token);

        // Check if amount is allowed to spend the token
        require(token.allowance(msg.sender, address(this)) >= amount, "Staking: Must allow Spending");

        // Transfer the token to contract
        token.transferFrom(msg.sender, address(this), amount);

        // Calculate the last reward
        uint uncalculatedReward = _calculateReward(_poolId, msg.sender);

        // Update the stake details
        stakeHolders[msg.sender][_poolId].stake += amount;
        stakeHolders[msg.sender][_poolId].reward += uncalculatedReward;
        stakeHolders[msg.sender][_poolId].lastRewardCalculated = block.timestamp;
        stakeHolders[msg.sender][_poolId].APY = pools[_poolId].APY;

        // Update TVL
        pools[_poolId].tvl += amount;

        // Add into the array
        stakers[_poolId].add(msg.sender);
        emit Staked(msg.sender, amount, _poolId, block.timestamp);
    }

    /**
     * @dev Calculate the current reward, add with previous reward, Transefer
     * it to sender and update reward to 0
     * @param _poolId Pool from which user want to claim the reward.
     * @notice This function will transfer the reward earned till now.
     */
    function claimReward(uint _poolId) public poolExists(_poolId){
        // Calculate the last reward
        uint uncalculatedReward = _calculateReward(_poolId, msg.sender);

        // Check for the allowance and transfer from the owners account
        require(
            pools[_poolId].totalAllotedReward > stakeHolders[msg.sender][_poolId].reward + uncalculatedReward,
            "Staking: Insufficient reward allowance from the Admin"
        );

        // Transfer the reward.
        IERC20 token = IERC20(pools[_poolId].token);
        token.transfer(msg.sender, stakeHolders[msg.sender][_poolId].reward + uncalculatedReward);

        // Update pools stats
        pools[_poolId].totalAllotedReward -= (stakeHolders[msg.sender][_poolId].reward + uncalculatedReward);
        pools[_poolId].totalClaimedReward += (stakeHolders[msg.sender][_poolId].reward + uncalculatedReward);

        // Update the stake details
        stakeHolders[msg.sender][_poolId].reward = 0;
        stakeHolders[msg.sender][_poolId].lastRewardCalculated = block.timestamp;

        // Remove if user don't have any reward or stake amount left.
        if (
            stakeHolders[msg.sender][_poolId].stake == 0 
            && 
            stakeHolders[msg.sender][_poolId].reward == 0
        ){
            stakers[_poolId].remove(msg.sender);
        }

        // Trigger the event
        emit RewardClaimed(msg.sender, stakeHolders[msg.sender][_poolId].reward + uncalculatedReward, _poolId, block.timestamp);
    }

    /**
     * @dev Sum the unstakable amount delete and transfer the request to _stakeHolder
     * @notice This function will transfer the unstable amount to your wallet
     * @param _poolId The pool in which user wants to unstake.
     * @param amount The amount user wants to add into his stake
     */
    function unstake(uint _poolId, uint amount) public poolExists(_poolId){

        // Check if user have sufficient amount staked.
        require(
            stakeHolders[msg.sender][_poolId].stake >= amount, 
            "Staking: You don't have enough unstakeble amount"
        );

        // Calculate the last reward
        uint uncalculatedReward = _calculateReward(_poolId, msg.sender);

        // Update the stake details
        stakeHolders[msg.sender][_poolId].stake -= amount;
        stakeHolders[msg.sender][_poolId].reward += uncalculatedReward;
        stakeHolders[msg.sender][_poolId].lastRewardCalculated = block.timestamp;

        // Update TVL
        pools[_poolId].tvl -= amount;

        // Remove if user don't have any reward or stake amount left.
        if (
            stakeHolders[msg.sender][_poolId].stake == 0 
            && 
            stakeHolders[msg.sender][_poolId].reward == 0
        ){
            stakers[_poolId].remove(msg.sender);
        }

        // Send the unstaked amout to stakeHolder
        IERC20 token = IERC20(pools[_poolId].token);
        token.transfer(msg.sender, amount);

        // Emit unstaked event
        emit Unstaked(msg.sender, amount, _poolId, block.timestamp);
    }

    /**
     * @dev Returns array of stakeholder
     * @notice This function will give list of stakers who have staked any amount.
     * @param _poolId The pool for which you want to get list of stakeHolders.
     * @return stakers.array the list os stakeholders
     */
    function getStakers(uint _poolId) public view returns(address[] memory) {   
        return stakers[_poolId].array;
    }

    /** 
     * @dev Calculate and return total undelivered rewards till now.
     * @notice This function will give you total of unclaimed rewards till now.
     * @param _poolId The pool for which you want to calculate total reward.
     * @return _totalReward Total unclaimed reward till now.
     */
    function totalReward(uint _poolId) public view returns (uint _totalReward) {
        uint sum = 0;
        for (uint i=0; i<stakers[_poolId].array.length; i++){
            sum += stakeHolders[stakers[_poolId].array[i]][_poolId].reward;
            sum += _calculateReward(_poolId, stakers[_poolId].array[i]);
        }
        _totalReward = sum;
    }

    /**
     * @dev Function to check if contract have suffecient reward allowance or not
     * @notice This function will return if it has sufficient fund for paying the reward
     * @param _poolId The pool for which you want to check reward availibility
     * @return True if have sufficient allowance for paying all the rewards
     */
    function haveSuffecientFundsForReward(uint _poolId) public view returns(bool) {
        return pools[_poolId].totalAllotedReward >= totalReward(_poolId);
    }

}

